if.foreach:
update
tb_article_tag a
set a.tag_id=#{item}
where a.article_id=#{articleId}

mapper.select:
select a.id as id, a.article_title as article_title, c.category_name as category_name, a.create_time as create_time, a.update_time as update_time, a.is_top as is_top, b.tag_name as tag_name from tb_article a,tb_tag b,tb_category c,tb_article_tag d where a.id=d.article_id AND b.id=d.tag_id AND a.category_id=c.id AND a.is_delete=0
AND a.article_title like CONCAT(CONCAT('%',#{title}),'%')
LIMIT #{current},#{size}
